import axios from "axios";

let user = false;
const token = localStorage.getItem("token");

axios.get("http://localhost:9000/auth", {
    headers: {
        "Authorization": `Bearer ${token}`
    }
}).then(data => {
    user = true;
}).catch(err => {
    return err;
});

export const isLoggedIn = user;
